/* font declarations */
@font-face {
    font-family: GothamBook;
    src: url('GothamBookRegular.a26e4396.otf');
}

@font-face {
    font-family: GothamLight;
    src: url('GothamLightRegular.968e6313.otf');
}

/* ========= */
/* font base */
/* ========= */

*, *:after, *:before,
h1, h2, h3, h4, h5, h6,
p, div, span {
    font-family: 'GothamBook', Arial, Helvetica, sans-serif;
}

/* ========= */
/* backgrounds base */
/* ========= */

.primaryBg {
    background-color: #007DC3;
}

.secondaryBg {
    background-color: #81C153;
}

/* ========= */
/* anchor tags */
/* ========= */

a {
    text-decoration: none;
}

.primary {
    color: #007DC3;
}

.secondary {
    color: #81C153;
}

.pageText {
    color: #4A4A4A;
    font-size: 16px;
}

.primaryBorder {
    border: 1px solid #007DC3;
}

.secondaryBorder {
    border: 1px solid #81C153;
}

.finePrint {
    color: #BFBDBF;
}

/* ========= */
/* flex base */
/* ========= */

.verticalCenter {
    display: flex;
    flex-direction: column;
    justify-content: center;
}

/* ========= */
/* header */
/* ========= */

.header {
    padding: 14px 20px;
    height: 156px;
}

.logoWrap, .nav {
    height: 100%;
}

/* ========= */
/* header logo */
/* ========= */

.logo {
    display: block;
    height: 100%;
    width: 100%;
    color: transparent;
    background-image: url('logo.fbae0e98.png');
    background-repeat: no-repeat;
    background-position: left 30%;
    background-size: 100% auto;
    max-width: 218px;
}

.logo:hover, .logo:focus, .logo:active, .logo:visited {
    color: transparent;
}

/* ========= */
/* header nav */
/* ========= */

.navList {
    list-style: none;
    padding: 0 0 0 0;
    margin: 0 0 0 0;
}

.navItem {
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding-bottom: 25px;
}

.navItem a {
    text-decoration: none;
    text-align: center;
    font-size: 16px;
}

.navItem a.loginLink {
    border: 1px solid #007DC3;
    padding: 10px 0;
}

/* ========= */
/* tel number link */
/* ========= */

.telWrap {
    text-align: right;
    font-size: 22px;
}

/* ========= */
/* footer base */
/* ========= */

.footer {
    padding: 40px 40px 20px;
    color: #ffffff;
}

.footerColumn {
    padding: 0 10px;
}

.footerColumn:first-child {
    padding-left: 0;
}

.footerColumn:last-child {
    padding-right: 0;
}

.footer h3 {
    font-weight: 400;
    font-size: 20px;
}

.footerList {
    list-style: none;
    padding: 0 0 0 0;
    margin: 0 0 0 0;
}

.footerList li {
    margin: 15px 0;
    padding: 0 0 0 0;
    font-size: 15px;
}

.contactInfo a {
    color: white;
    line-height: 1.4;
    margin-bottom: 15px;
    display: inline-block;
}

.copyright {
    text-align: center;
    padding: 20px 0 0 0;
    opacity: 0.7;
}

/* ========= */
/* sections base */
/* ========= */

section {
    min-height: 500px;
}

section .coverWrap {
    position: relative;
}

section .cover {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
}

/* ========= */
/* overlays */
/* ========= */

.overlay {
    background: rgba(0, 0, 0, 0.4);
}

.blur {
    filter: blur(1px);
}

/* ========= */
/* hero section */
/* ========= */

.hero {
    background-image: url('dispensing.d9cd4fbb.png');
    background-size: cover;
    background-position: center center;
    background-repeat: no-repeat;
}

.heroText {
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    color: #ffffff;
    text-align: center;
    padding: 60px;
}

.heroText h1 {
    font-weight: 400;
    margin: 0 0 0 0;
}

.heroText p {
    margin: 5px auto 30px;
    max-width: 430px;
    text-align: center;
}

/* ========= */
/* cta buttons */
/* ========= */

button.cta {
    border: 2px solid #ffffff;
    color: #ffffff;
    /*width: 120px;*/
    /*padding: 12px 14px 14px;*/
    margin: 0 auto;
    cursor: pointer;
    background-color: transparent;
    transition: all 0.2s ease;
    padding: .3em 1em;
    font-size: 20px;
    position: relative;
    line-height: 1.7em;
}

button.cta:after {
    display: inline-block;
    position: absolute;
    margin-left: -1em;
    opacity: 0;
    font-family: "Font Awesome 5 Free"; font-weight: 400;
    content: "\f054";
    transition: all .2s ease;
    font-style: normal;
    font-variant: normal;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
}

button.cta:hover:after {
    margin-left: 0;
    opacity: 1;
}

button.cta:hover {
    padding: .3em 2em .3em .7em;
    border: 2px solid transparent;
    background-color: rgba(255,255,255,.2);
}

/* ======== */
/* main body panel */
/* ========= */

.bodySection {
    padding: 80px 34px;
}

.fig {
    margin: 0 0 0 0;
}

.aboutPanel {
    padding-right: 34px;
}

.aboutPanel h2 {
    margin: 0 0 0 0;
    font-weight: 400;
}

.aboutPanel h4 {
    font-weight: 400;
}

.blurbContent {
    display: flex;
}

.blurbText {
    line-height: 1.6;
}

.aboutPanel .fig {
    width: 100%;
    height: 100%;
    margin: 0 0 0 0;
    padding: 5px 15px 0 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.aboutPanel .fig img {
    max-width: 125px;
    max-height: 103px;
}

.withFigure p {
    margin: 0 0 0 0;
}

.forClientsPanel {
    padding: 0 0 0 34px;
}

.shadowBox {
    border-radius: 4px;
    box-shadow: 0 14px 20px 1px rgba(0,0,0,.08);
    /*box-shadow: 0 0 6px 1px rgba(0, 0, 0, 0.5);*/
}

.panelBlurb {
    padding: 20px;
}

.forClientsPanel h2 {
    font-weight: 400;
}

.bodyBlurb.bottom {
    padding-top: 20px;
}

.clientCta {
    padding: 14px 34px 12px;
    margin: 34px auto 0;
    width: 100%;
    display: block;
    transition: all 0.2s ease-in;
}

.clientCta:hover {
    background: #81C153;
    color: #ffffff;
    cursor: pointer;
    border-radius: 0;
}

/* ======== */
/* fine print section */
/* ========= */

section.finePrintSection {
    min-height: 400px;
    padding-bottom: 100px;
}

.finePrint {
    max-width: 590px;
    margin: 0 auto;
}
